import React, { useState, useEffect } from "react";

const App = () => {
  const [data, setData] = useState({});

  const fetchData = async () => {
    const res = await fetch("https://api.agify.io/?name=Dhruvil");
    const data = await res.json();
    setData(data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  return <>{data.name} 
  {data.age}</>;
}
export default App;
