import React, { useEffect, useState } from "react";
import "./styles.css";

const App = () => {
  const [loading, setLoading] = useState(true);
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 5000);
    return () => clearTimeout(timer);
  }, []);

  if (loading) {
    return "Loading...";
  }

  return <div>Hello world</div>;
};

export default App;
