import React, { createContext } from "react";
import ComA from "./context/ComA";

const user = {
  FirstName: "Dhruvil",
  LastName: "Suhagiya",
};
const fullName = createContext({ user });

const App = () => {
  return (
    <>
      <fullName.Provider value={user}>
        <ComA />
      </fullName.Provider>
    </>
  );
};

export default App;
export { fullName };
